import React, { useState } from "react";

const Todo = () => {
  const [todo, setTodo] = useState([]);
  const [itemName, setItemName] = useState("");
  const handleTodo = () => {
    setTodo([...todo, itemName]);
    setItemName("");
  };
  return (
    <div>
      <input
        type="text"
        placeholder="Add a task"
        value={itemName}
        onChange={(e) => {
          setItemName(e.target.value);
        }}
      />
      <button onClick={handleTodo} style={{marginLeft:"10px"}}>Add a todo</button>
      <button style={{marginLeft:"10px"}}>Logout</button>

      {todo.length > 0 ? (
        <div style={{ display: "flex", flexWrap: "wrap", marginTop: "20px" }}>
          {todo.map((item, index) => (
            <div
              key={index}
              style={{
                display: "flex",
                alignItems: "center",
                flexDirection: "column",
                border: "2px solid black",
                padding: "30px",
              }}
            >
              <div>
                <h3>{item}</h3>
              </div>
              <div>
                <button style={{ height: "fit-content", marginLeft: "10px" }}>
                  Done
                </button>
                <button style={{ height: "fit-content", marginLeft: "10px" }}>
                  Update
                </button>
                <button style={{ height: "fit-content", marginLeft: "10px" }}>
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div>Please add a list</div>
      )}
    </div>
  );
};

export default Todo;
