import React, { useState, useEffect } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [loginValue, setLoginValue] = useState({
    email: "",
    password: "",
  });

  const navigate = useNavigate();

  useEffect(() => {
    try {
      if (
        localStorage.getItem("token") &&
        jwtDecode(localStorage.getItem("token")).exp * 1000 < Date.now()
      ) {
        localStorage.removeItem("token");
      }

      if (
        localStorage.getItem("token") &&
        jwtDecode(localStorage.getItem("token")).exp * 1000 > Date.now()
      ) {
        navigate("/todo");
      }
    } catch (error) {
      localStorage.removeItem("token");
    }
  }, [navigate]);

  const handleSubmit = () => {
    if (loginValue.email === "") {
      alert("Enter email");
    } else if (loginValue.password === "") {
      alert("Enter password");
    } else {
      axios
        .post("http://localhost:5200/user/login", {
          email: loginValue.email,
          password: loginValue.password,
        })
        .then((res) => {
          if (res?.data?.status === true) {
            localStorage.setItem("token", res?.data?.token);
            navigate("/todo");
          }
        })
        .catch((err) => {
          alert(`${err.response?.data.msg}`);
        });
    }
  };
  return (
    // <div
    //   style={{
    //     display: "flex",
    //     flexDirection: "column",
    //     justifyContent: "center",
    //     alignItems: "center",
    //     height: "90vh",
    //   }}
    // >
    //   <input
    //     type="text"
    //     placeholder="Enter email id"
    //     style={{ height: "40px", width: "250px", paddingLeft: "10px" }}
    //     value={loginValue.email}
    //     onChange={(e) => {
    //       setLoginValue({ ...loginValue, email: e.target.value });
    //     }}
    //   />
    //   <input
    //     type="password"
    //     placeholder="Enter password"
    //     value={loginValue.password}
    //     style={{
    //       height: "40px",
    //       width: "250px",
    //       paddingLeft: "10px",
    //       marginTop: "10px",
    //     }}
    //     onChange={(e) => {
    //       setLoginValue({ ...loginValue, password: e.target.value });
    //     }}
    //   />
    //   <br />
    //   <p style={{ margin: "0", padding: "0",textDecoration:"underline",cursor: "pointer" }}>Forgot Password?</p>
    //   <button onClick={handleSubmit}>Login</button>
    //   <button onClick={()=>{
    //     navigate("/signup")
    //   }}>SignUp</button>
    // </div>
    <div class="container">
      <input type="checkbox" id="flip" />
      <div class="cover">
        <div class="front">
          <img src="image/thumb (1).jpg" alt="" />
          <div class="text">
            <span class="text-1">
              Every new friend is a <br />
              new adventure
            </span>
            <span class="text-2">Let's get connected</span>
          </div>
        </div>

        <div class="back">
          <img class="backImg" src="image/ss.jpg" alt="" />
          <div class="text">
            <span class="text-1">
              Complete miles of journey <br />
              with one step
            </span>
            <span class="text-2">Let's get started</span>
          </div>
        </div>
      </div>
      <div class="forms">
        <div class="form-content">
          <div class="login-form">
            <div class="title">Login</div>
            <div class="input-boxes">
              <div class="input-box">
                <i class="fas fa-envelope"></i>
                <input
                  type="text"
                  name="email"
                  placeholder="Enter your email"
                  required
                />
              </div>
              <div class="input-box">
                <i class="fas fa-lock"></i>
                <input
                  type="password"
                  name="password"
                  placeholder="Enter your password"
                  required
                />
              </div>
              <div class="text">
                <a href="#">Forgot password?</a>
              </div>
              <div class="button input-box">
                <input type="submit" value="Submit" name="login" />
              </div>
              <div class="text sign-up-text">
                Don't have an account? <label for="flip">Sigup now</label>
              </div>
            </div>
          </div>
          <div class="signup-form">
            <div class="title">Signup</div>
            <div class="error">
              <br />
            </div>
            <div class="input-boxes">
              <div class="input-box">
                <i class="fas fa-envelope"></i>
                <input
                  type="text"
                  name="email"
                  placeholder="Enter your email"
                  required
                />
              </div>
              <div class="input-box">
                <i class="fas fa-lock"></i>
                <input
                  type="password"
                  name="password"
                  placeholder="Enter your password"
                  required
                />
              </div>
              <div class="button input-box">
                <input type="submit" value="Submit" name="signup" />
              </div>
              <div class="text sign-up-text">
                Already have an account? <label for="flip">Login now</label>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
