import React, { useState,useEffect } from "react";
import axios from "axios";
import { jwtDecode } from "jwt-decode";
import {useNavigate} from 'react-router-dom'
import '../css/login.css';

const Login = () => {
  const [loginValue, setLoginValue] = useState({
    email: "",
    password: "",
  });

  const navigate = useNavigate();

  useEffect(() => {
    try {
      if (
        localStorage.getItem("token") &&
        jwtDecode(localStorage.getItem("token")).exp * 1000 < Date.now()
      ) {
        localStorage.removeItem("token");
      }

      if (
        localStorage.getItem("token") &&
        jwtDecode(localStorage.getItem("token")).exp * 1000 > Date.now()
      ) {
        navigate("/todo");
      }
    } catch (error) {
      localStorage.removeItem("token");
    }
  }, [navigate]);

  const handleSubmit = () => {
    if(loginValue.email === "")
    {
      alert("Enter email")
    }
    else if(loginValue.password === "")
    {
      alert("Enter password")
    }
    else
    {
      axios
        .post("http://localhost:5200/user/login", {
          email: loginValue.email,
          password: loginValue.password,
        })
        .then((res) => {
          if(res?.data?.status === true)
          {
            localStorage.setItem("token",res?.data?.token);
            navigate("/todo");
          }
        })
        .catch((err) => {
          alert(`${err.response?.data.msg}`);
        });
    }
  };
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        height: "90vh",
      }}
      className="login_body"
    >
      <input
        type="text"
        placeholder="Enter email id"
        style={{ height: "40px", width: "250px", paddingLeft: "10px" }}
        value={loginValue.email}
        onChange={(e) => {
          setLoginValue({ ...loginValue, email: e.target.value });
        }}
      />
      <input
        type="password"
        placeholder="Enter password"
        value={loginValue.password}
        style={{
          height: "40px",
          width: "250px",
          paddingLeft: "10px",
          marginTop: "10px",
        }}
        onChange={(e) => {
          setLoginValue({ ...loginValue, password: e.target.value });
        }}
      />
      <br />
      <p style={{ margin: "0", padding: "0",textDecoration:"underline",cursor: "pointer" }}>Forgot Password?</p>
      <button onClick={handleSubmit}>Login</button>
      <button onClick={()=>{
        navigate("/signup")
      }}>SignUp</button>
    </div>
  );
};

export default Login;
