import React, { useState } from "react";

const Todo = () => {
  const [list, setList] = useState("");
  const [itemArr, setItemArr] = useState([]);
  const [update, setUpdate] = useState("Add to task");
  const [edit, setEdit] = useState("");
  const [sty, setSty] = useState("none");
  const [done, setDone] = useState("Done");
  return (
    <div>
      <h1 style={{ textAlign: "center" }}>TO DO LIST</h1>
      <input
        type="text"
        value={list}
        onChange={(e) => {
          setList(e.target.value);
        }}
      />
      <button
        onClick={() => {
          if (update === "Add to task") {
            const obj = { name: list };
            setItemArr([...itemArr, obj]);
            setList("");
          } else {
            const as = itemArr.map((it, index) => {
              return {
                name: index === edit ? list : it.name,
              };
            });
            setItemArr(as);
            setUpdate("Add to task");
            setList("");
          }
        }}
      >
        {update}
      </button>
      <div style={{ display: "flex", flexDirection: "row", flexWrap: "wrap" }}>
        {itemArr.map((item) => (
          <div
            style={{
              border: "5px black solid",
              height: "100px",
              width: "200px",
              margin: "10px",
              textAlign: "center",
            }}
          >
            <h4 style={{ textDecoration: `${sty}` }}> Name : {item.name}</h4>

            <button
              onClick={() => {
                if (done === "Done") {
                  setDone("UnDone");
                  setSty("line-through");
                } else if (done === "UnDone") {
                  setSty("none");
                  setDone("Done");
                }
              }}
            >
              {done}
            </button>
            <button
              onClick={() => {
                const as = itemArr.filter((it) => it !== item);
                setItemArr(as);
              }}
            >
              DELETE
            </button>
            <button
              onClick={() => {
                setUpdate("Update");
                setList(item.name);
                const as = itemArr.findIndex((it) => it === item);
                setEdit(as);
              }}
            >
              EDIT
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Todo;
