import React,{useState} from 'react'

const Todo = () => {
    const [todo, setTodo] = useState([])
    const [itemName, setItemName] = useState("")
    const handleTodo = () => {
        setTodo([...todo,itemName]);
        setItemName("");
    }
  return (
    <div>
        <input type='text' placeholder='Add a task' value={itemName} onChange={(e)=>{
            setItemName(e.target.value);
        }}/>
        <button onClick={handleTodo}>Add a todo</button>

        {
            todo.length > 0 ? 
            <div>
            </div>
            :
            <div>
                Please add a list
            </div>
        }
    </div>
  )
}

export default Todo
