import React, { useState } from "react";

const Todo = () => {
  const [todo, setTodo] = useState([]);
  const [itemName, setItemName] = useState("");
  const handleTodo = () => {
    setTodo([...todo, itemName]);
    setItemName("");
  };
  return (
    <div>
      <input
        type="text"
        placeholder="Add a task"
        value={itemName}
        onChange={(e) => {
          setItemName(e.target.value);
        }}
      />
      <button onClick={handleTodo}>Add a todo</button>

      {todo.length > 0 ? (
        <div>
          {todo.map((item, index) => (
            <div key={index} style={{display:"flex",alignItems:"center"}}>
                <h3>{item}</h3>
                <button style={{height:"fit-content"}}>Update</button>
                <button style={{height:"fit-content"}}>Delete</button>
            </div>
          ))}
        </div>
      ) : (
        <div>Please add a list</div>
      )}
    </div>
  );
};

export default Todo;
