import React, { useState,useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {jwtDecode} from 'jwt-decode';

const Todo = () => {
  const navigate = useNavigate();
  const [itemName, setItemName] = useState("");
  const [todo, setTodo] = useState([])

  const getAllTodo = () => {
    const email = jwtDecode(localStorage.getItem("token")).email;
    if (email) 
    {
      axios.post("http://localhost:5200/todo/get",{
        email
      })
      .then((res)=>{
        setTodo(res?.data?.todo);
      })
      .catch((err)=>{
        console.log('err', err)
      })
    }
  }
  useEffect(() => {
    getAllTodo();
    // eslint-disable-next-line
  }, [])


  useEffect(() => {
    try {
      if (
        localStorage.getItem("token") &&
        jwtDecode(localStorage.getItem("token")).exp * 1000 < Date.now()
      ) {
        localStorage.removeItem("token");
        navigate("/");
      }
    } catch (error) {
      localStorage.removeItem("token");
    }
  }, [navigate]);

  const handleTodo = () => {
    const email = jwtDecode(localStorage.getItem("token")).email;
    if (email) {
      axios.post("http://localhost:5200/todo/add", {
        email,
        name: itemName,
      }).then((res)=>{
        if(res)
        {
          alert("Added to list!")
        }
      }).catch((err)=>{
        console.log('err', err);
      })
    }
    setItemName("");
  };
  return (
    <div>
      <input
        type="text"
        placeholder="Add a task"
        value={itemName}
        onChange={(e) => {
          setItemName(e.target.value);
        }}
      />
      <button onClick={handleTodo} style={{ marginLeft: "10px" }}>
        Add a todo
      </button>
      <button
        style={{ marginLeft: "10px" }}
        onClick={() => {
          localStorage.removeItem("token");
          navigate("/");
        }}
      >
        Logout
      </button>

      {todo.length > 0 ? (
        <div style={{ display: "flex", flexWrap: "wrap", marginRight: "20px" }}>
          {todo.map((item, index) => (
            <div
              key={index}
              style={{
                display: "flex",
                alignItems: "center",
                flexDirection: "column",
                border: "2px solid black",
                padding: "30px",
              }}
            >
              <div>
                <h3>{item.name}</h3>
              </div>
              <div>
                <button style={{ height: "fit-content", marginLeft: "10px" }}>
                  Done
                </button>
                <button style={{ height: "fit-content", marginLeft: "10px" }}>
                  Update
                </button>
                <button style={{ height: "fit-content", marginLeft: "10px" }}>
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div>Please add a list</div>
      )}
    </div>
  );
};

export default Todo;
